let menu=document.querySelector(".header-wrap");function animation(){document.querySelector(".header-company-name").style.animationDuration="2s"}document.querySelector(".burger-menu").addEventListener("click",(()=>{menu.classList.toggle("burger-menu-active")})),document.querySelector(".interior-menu").addEventListener("click",(e=>{e.target.classList.contains("interior-menu__text")&&(document.querySelectorAll(".interior-menu__text").forEach((e=>e.classList.remove("interior-menu__text_active"))),e.target.classList.add("interior-menu__text_active"))})),document.querySelector(".header-menu").addEventListener("click",(e=>{e.target.classList.contains("header-menu__link")&&(document.querySelectorAll(".header-menu__link").forEach((e=>e.classList.remove("header-menu__link_active"))),e.target.classList.add("header-menu__link_active"))})),animation();